 // src/App.js
 import React, { useState } from 'react';
 import './App.css';
 import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
 import Survey from './components/Survey';
 import Navigation from './components/Navigation';
 import Dashboard from './components/Dashboard';

function App() {
  const [clubs, setClubs] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [showSurvey, setShowSurvey] = useState(false);

  const handleSurveyButtonClick = () => {
    // showSurvey ? setShowSurvey(false) : setShowSurvey(true);
    <Router>
      <Routes>
      <Route path="/club_survey" component={<Survey/>}/>
      </Routes>
    </Router>
  };

  const handleSurveySubmit = (answers) => {
    // Here you would send the survey answers to your AI service for analysis
    // For now, we'll just simulate recommendations based on the answers
    // Simulated recommendations
    const recommendedClubs = [
      { id: 1, name: 'Chess Club', category: 'Strategy', description: 'A club for chess enthusiasts.' },
      { id: 2, name: 'Art Club', category: 'Creative', description: 'A club for art lovers.' },
      // Add more recommended clubs based on the survey answers
    ];
    setClubs(recommendedClubs);
    setShowSurvey(false); // Hide the survey after submission
  };

  return (
    <Router>
    <div className="App">
      <Navigation onSurveyButtonClick={handleSurveyButtonClick} />
      {showSurvey && <Survey onSurveySubmit={handleSurveySubmit} />}
      {!showSurvey && (
        <div>
          {/* Your club filtering and display code goes here */}
        </div>
      )}

    </div>
    </Router>
  );
}

export default App;


